mapper.insert:
INSERT INTO Displays_Setting
(
    display_nm, gpu_nm, pos_x, pos_y, width, height, primary_fl
)
VALUES
(
    #{displayNm}, #{gpuNm}, #{posX}, #{posY}, #{width}, #{height}, #{primaryFl}
)

mapper.select:
SELECT COUNT(display_id)
FROM Displays_Setting
WHERE display_id = #{displayId}

