mapper.select:
SELECT *
FROM pixelviewer.playlist_contents
WHERE content_id IN ${contentIdList}

SELECT count(playlist_id)
FROM Playlists
WHERE playlist_nm = #{playlistNm}
    AND rowsize = #{rowsize}
    AND columnsize = #{columnsize}

mapper.insert:
INSERT INTO Playlists
(
    playlist_nm, layer_id, preset_id, screen_id, content_id_list
)
VALUES
(
    #{playlistNm}, #{layerId}, #{presetId}, #{screenId}, #{contentIdList}
)

mapper.select:
SELECT COUNT(playlist_id)
FROM Playlists
WHERE Playlists.playlist_id = #{playlistId}

mapper.update:
DELETE S
FROM Playlists AS S
WHERE S.playlist_id = #{playlistId}

mapper.select:
SELECT COUNT(playlist_id)
FROM Playlists
WHERE Playlists.playlist_id = #{playlistId}

mapper.update:
UPDATE Playlists
SET playlist_nm = #{playlistNm}
WHERE Playlists.playlist_id = #{playlistId}

mapper.select:
SELECT COUNT(playlist_id)
FROM Playlists
WHERE Playlists.playlist_id = #{playlistId}

mapper.update:
UPDATE Playlists
SET playlist_nm = #{playlistNm}, rowsize = #{rowsize}, columnsize = #{columnsize}
WHERE Playlists.playlist_id = #{playlistId}

mapper.insert:
INSERT INTO Playlist_Items
(
    ord_no, playlist_id, type
    , cts_nm, cts_path, playtime
    , weather_fl, air_info_fl
)
VALUES
(
    #{ordNo}, #{playlistId}, #{type}
    , #{ctsNm}, #{ctsPath}, #{playtime}
    , #{weatherFl}, #{airInfoFl}
)

INSERT INTO playlist_contents
(
    type
    , cts_nm, cts_path, playtime
    , weather_fl, air_info_fl
)
VALUES
    (
        #{type}
        , #{ctsNm}, #{ctsPath}, #{playtime}
        , #{weatherFl}, #{airInfoFl}
    )

mapper.select:
