mapper.select:
SELECT *
FROM Presets AS P
WHERE P.preset_id = #{presetId}

SELECT *
FROM Layers AS L
WHERE L.preset_id = #{presetId}

SELECT count(preset_id)
FROM Presets
WHERE preset_nm = #{presetNm}
    AND rowsize = #{rowsize}
    AND columnsize = #{columnsize}

SELECT COUNT(preset_id)
FROM Presets
WHERE Presets.preset_id = #{presetId}

mapper.update:
DELETE P
FROM Presets AS P
WHERE P.preset_id = #{presetId}

mapper.select:
SELECT COUNT(preset_id)
FROM Presets
WHERE Presets.preset_id = #{presetId}

mapper.update:
UPDATE Presets
SET preset_nm = #{presetNm}
WHERE Presets.preset_id = #{presetId}

mapper.select:
SELECT COUNT(preset_id)
FROM Presets
WHERE Presets.preset_id = #{presetId}

mapper.update:
UPDATE Presets
SET
    preset_nm = #{presetNm}
  , rowsize = #{rowsize}
  , columnsize = #{columnsize}
  , user_style_yn = #{userStyleYn}
  , preset_status = "WAIT"
WHERE Presets.preset_id = #{presetId}

DELETE L
FROM Layers AS L
WHERE L.preset_id = #{presetId}

mapper.select:
SELECT *
FROM Layers AS L
WHERE L.preset_id = #{presetId}

mapper.update:
UPDATE Presets
SET preset_status = #{presetStatus}
WHERE Presets.preset_id = #{presetId}
