mapper.select:
SELECT object_id
FROM LayerObjects AS O
WHERE O.layer_id = #{layerId}
    AND type = #{type}

mapper.insert:

    SELECT LAST_INSERT_ID()


INSERT INTO LayerObjects (layer_id, preset_id, screen_id, type, object_nm)
VALUES (#{layerId}, #{presetId}, #{screenId}, #{type}, #{objectNm});

INSERT INTO Subtitles (object_id, pos_x, pos_y, width, height, ord, scroll_way, scroll_speed, scroll_start_location, subtitle_style)
VALUES (LAST_INSERT_ID(), #{posX}, #{posY}, #{width}, #{height}, #{ord}, #{scrollWay}, #{scrollSpeed}, #{scrollStartLocation}, #{subtitleStyle});

mapper.update:
UPDATE Subtitles
SET
    pos_x = #{posX},
    pos_y = #{posY}
WHERE subtitle_id = #{subtitleId}

UPDATE Subtitles
SET
    width = #{width},
    height = #{height}
WHERE subtitle_id = #{subtitleId}

UPDATE Subtitles
SET subtitle_style = #{subtitleStyle}
WHERE subtitle_id = #{subtitleId}

UPDATE Subtitles
SET
    scroll_way = #{scrollWay},
    scroll_speed = #{scrollSpeed},
    scroll_start_location = #{scrollStartLocation}
WHERE subtitle_id = #{subtitleId}
